// Helper functions to manipulate DOM, format numbers and debounce
export var // Select single element
select = function select(el) {
  return document.querySelector(el);
},
    // Select Nodelist array of elements
selectAll = function selectAll(els) {
  return document.querySelectorAll(els);
},
    // Select elements by id
getId = function getId(id) {
  return document.getElementById(id);
},
    // Capitalise string
caps = function caps(str) {
  return str.charAt(0).toUpperCase() + str.slice(1);
},
    each = function each(array, callback) {
  for (var i = 0, j = array.length; i < j; i++) {
    callback.call(i, array[i]);
  }
},
    rect = function rect(el) {
  return el.getBoundingClientRect();
},
    offSet = function offSet(el) {
  var rect = el.getBoundingClientRect();
  return {
    top: rect.top + window.pageYOffset,
    left: rect.left + window.pageXOffset
  };
},
    // Multi addEventListener, takes multiple parameters ('click load change etc')
listen = function listen(el, s, fn) {
  return each(s.split(' '), function (e) {
    return el.addEventListener(e, fn, false);
  });
};
